#include<stdio.h>
void mergesort(int low,int high);
void merge(int low,int mid,int high);
void main()
{
    printf("Enter the size of the array\n");
    scanf("%d",&n);
    printf("Enter the elements into the array");
    for(i=0;i<n;i++)
    {
        scanf("%d",&a[i]);
    }
    printf("The unsorted array: ");
    for(i=0;i<n;i++)
    {
        printf("%d\t",a[i]);
    }
    printf("\n");
    l=0;
    h=n-1;
    mergesort(l,h);
}
void mergesort(int low,int high)
{
    if(low<high)
    {
        mid=(low+high)/2;
        mergesort(l,mid);
        mergesort(mid+1,high);
        merge(low,mid,high);
    }
}
void merge(int low,int mid,int high)
{
   i=low;
   k=low;
   j=mid+1;
   while(i<=mid&&j<=high)
   {
        if(a[i]<a[j])
        {
            b[k]=a[i];
            i++;
            k++;
        }
        else 
        {
            b[k]=a[j];
            j++;
            k++;
        }
   } 
   while(i<=mid)
   {
    b[k]=a[i];
    k++;
    i++;
   }
   while(j<=high)
   {
    b[k]=a[j];
    k++;
    j++;
   }
}





















































































































































































































































































































































































































































































































































































































































































































